import React from "react";

// not sure why to add the below
this.handleScroll = this.handleScroll.bind(this);
class Home extends React.Component {
  constructor(props) {
    super(props);

    this.bodyRef = React.createRef();
    this.alertingRef = React.createRef();
    this.changeMeRef = React.createRef();
    this.state = {
      myBody: this.bodyRef,
      myChangeMe: this.changeMeRef,
      myAlerting: this.alertingRef,
      scrollHeight: null,
      clientHeight: null,
      bodyOffset: null,
      visibleArea: null,
      scrollAmount: null,
      alertingOffset: null,
      alertingActive: null
    };
  }
  setChangeClass(myRef, myClass, myTimeout) {
    const changeMe = myRef;
    // this just adds the class no matter what
    // console.log("outside changeme setTimeout");
    const myChangeClassTimeout = setTimeout(() => {
      changeMe.current.classList.remove(myClass);
      console.log("inside changeme setTimeout");
    }, myTimeout);
    if (changeMe.current.classList.contains(myClass)) {
      console.log("clear timeout");
      clearTimeout(myChangeClassTimeout);
    }
    changeMe.current.classList.add(myClass);
  }

  setActiveChangeClass(
    cardOffsetFromState,
    visibleArea,
    scrollAmount,
    myStateKey,
    myTimeout
  ) {
    // this tests if the thing is in view
    if (scrollAmount > cardOffsetFromState - visibleArea) {
      // passes it to child
      const myAwesomeTimeout = setTimeout(() => {
        // console.log("i timed out");
        this.setState({ [myStateKey]: false });
      }, myTimeout);
      this.setState(prevState => {
        // console.log(prevState[myStateKey]);
        if (prevState[myStateKey] === true) {
          // console.log("clear timeout");
          clearTimeout(myAwesomeTimeout);
        }
        return { [myStateKey]: true };
      });
    }
  }
  handleClassAddingOnScroll(
    alertingOffset,
    visibleArea,
    scrollAmount,
    myRef,
    myClass,
    myTimeout
  ) {
    // this tests if the thing is in view
    if (scrollAmount > alertingOffset - visibleArea) {
      this.setChangeClass(myRef, myClass, myTimeout);
    }
  }

  handleScroll() {
    // https://reactjs.org/docs/faq-functions.html#how-can-i-prevent-a-function-from-being-called-too-quickly-or-too-many-times-in-a-row
    // this.emitChangeDebounced(e);
    // // If there's a timer, cancel it
    // if (this.timer) {
    //   window.cancelAnimationFrame(this.timer);
    // }
    // // Setup the new requestAnimationFrame()
    // this.timer = window.requestAnimationFrame(function() {
    //   // Run our scroll functions
    //   console.log("debounced");
    // });
    const scrollAmount = this.state.myBody.current.scrollTop;
    const alertingOffset = this.state.alertingOffset;
    const visibleArea = this.state.visibleArea;
    this.setState({
      scrollAmount: scrollAmount
    });
    window.requestAnimationFrame(() => {
      this.setChangeClass(this.state.myChangeMe, "blinkDivUpPlain", 1000);
      this.setActiveChangeClass(
        alertingOffset,
        visibleArea,
        scrollAmount,
        "alertingActive",
        5000
      );
    });
  }

  handleScrollBetter() {
    // console.log("no debounce");
    // function callMeLola() {
    //   // console.log("lolllllaaaaaaaaa");
    // }
    // function timeoutsetter() {
    //   clearTimeout(GlobalTimeout);
    //   var GlobalTimeout = setTimeout(callMeLola, 1500);
    // }

    Common.debounce(this.handleScroll(), 500);
  }
  setScrollingConstants() {
    const scrollHeight = this.state.myBody.current.scrollHeight;
    const clientHeight = this.state.myBody.current.clientHeight;
    const bodyOffset = this.state.myBody.current.offsetTop;
    const visibleArea =
      this.state.myBody.current.clientHeight +
      this.state.myBody.current.offsetTop;
    const scrollAmount = this.state.myBody.current.scrollTop;
    const alertingOffset = this.state.myAlerting.current.offsetTop;

    this.setState({
      scrollHeight: scrollHeight,
      clientHeight: clientHeight,
      bodyOffset: bodyOffset,
      visibleArea: visibleArea,
      scrollAmount: scrollAmount,
      alertingOffset: alertingOffset
    });
  }
  componentDidMount() {
    console.log("mounted");
    console.log(this.state.myBody);
    console.log(this.bodyRef.current);

    this.setScrollingConstants();
    this.bodyRef.current.addEventListener(
      "scroll",
      this.handleScrollBetter.bind(this)
    );
  }
  render() {
    const scrollData = {
      scrollHeight: this.state.scrollHeight,
      clientHeight: this.state.clientHeight,
      bodyOffset: this.state.bodyOffset,
      visibleArea: this.state.visibleArea,
      scrollAmount: this.state.scrollAmount,
      alertingOffset: this.state.alertingOffset,
      alertingActive: this.state.alertingActive
    };
    return (
      <div
        className="inner-body"
        // onScroll={this.handleScrollBetter}
        ref={this.bodyRef}
      >
        <div className="headline">
          Hi, I'm Amanda Innis, a designer &amp; creative technologist
        </div>

        <div className="card-wrapper">
          <div className="wrapper-padding constants-info">
            <ul>
              <li>
                window to wrapper
                <span>{this.props.scrollData.bodyOffset}</span>
              </li>
              <li>
                height of wrapper
                <span>{this.props.scrollData.clientHeight}</span>
              </li>

              <li>
                height visible
                <span>{this.props.scrollData.visibleArea}</span>
              </li>
              <li>
                height of contents
                <span>{this.props.scrollData.scrollHeight}</span>
              </li>
              <li>
                cards offset
                <span>{this.props.scrollData.alertingOffset}</span>
              </li>
              <li>
                <h4 className="mt-2">Math</h4>
              </li>

              <li>
                cards offset - height visible
                <span>
                  {this.props.scrollData.alertingOffset -
                    this.props.scrollData.visibleArea}
                </span>
              </li>
              <li>
                <h4 className="mt-2">Change</h4>
              </li>

              <li>
                amount scrolled
                <span ref={this.changeMeRef}>{scrollAmount}</span>
                <div>
                  the debounce article
                  https://dev.to/gabe_ragland/debouncing-with-react-hooks-jci
                </div>
              </li>
            </ul>
          </div>
          <h1 className="wrapper-padding section-header">Projects</h1>
          <div className="portfolio-container">
            <div className="portfolio-card-wrapper">
              <InsightAnalyticsCard></InsightAnalyticsCard>
            </div>
            <div className="portfolio-card-wrapper">
              <AlphaCapCard></AlphaCapCard>
            </div>
            <div className="portfolio-card-wrapper">
              <CRBCard></CRBCard>
            </div>
            <div ref={this.alertingRef} className="portfolio-card-wrapper">
              <AlertingCard active={alertingActive}></AlertingCard>
            </div>

            {/* jon added the squiggly so it immediately renders */}
            {/* {portfolioData.map((portfolio, i) => {
                return <PortfolioCard key={i} data={portfolio} />;
              })} */}
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
