import React from "react";
import IsLoading from "../reusable/IsLoading";
import * as Common from "../reusable/common";
import StockData from "../../data/stock_data";
const imgCRB = require("../../assets/images/crb-img.png");
const data = {
  id: 3,
  img: imgCRB,
  title: "CRB Pricer",
  subtitle: "Central Risk Book Pricing and Trade Execution",
  blurb: [""],
  bkgd: "var(--crb-bkgd)"
};

const allTickers = [
  {
    symbol: "ZOES",
    name: "Zoe&#39;s Kitchen, Inc.",
    last_sale: "12.73",
    market_cap: "$249.56M",
    IPOyear: "2014",
    sector: "Consumer Services",
    industry: "Restaurants",
    summary_quote: "https://www.nasdaq.com/symbol/zoes",
    FIELD9: ""
  },
  {
    symbol: "ZTS",
    name: "Zoetis Inc.",
    last_sale: "89.83",
    market_cap: "$43.28B",
    IPOyear: "2013",
    sector: "Health Care",
    industry: "Major Pharmaceuticals",
    summary_quote: "https://www.nasdaq.com/symbol/zts",
    FIELD9: ""
  },
  {
    symbol: "ZTO",
    name: "ZTO Express (Cayman) Inc.",
    last_sale: "17.97",
    market_cap: "$14.15B",
    IPOyear: "2016",
    sector: "Transportation",
    industry: "Trucking Freight/Courier Services",
    summary_quote: "https://www.nasdaq.com/symbol/zto",
    FIELD9: ""
  },
  {
    symbol: "ZUO",
    name: "Zuora, Inc.",
    last_sale: "23.36",
    market_cap: "$2.52B",
    IPOyear: "2018",
    sector: "Technology",
    industry: "Computer Software: Prepackaged Software",
    summary_quote: "https://www.nasdaq.com/symbol/zuo",
    FIELD9: ""
  },
  {
    symbol: "ZYME",
    name: "Zymeworks Inc.",
    last_sale: "16.55",
    market_cap: "$528.93M",
    IPOyear: "2017",
    sector: "Health Care",
    industry: "Major Pharmaceuticals",
    summary_quote: "https://www.nasdaq.com/symbol/zyme",
    FIELD9: ""
  }
];
class CRBCard extends React.Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.tickerInputRef = React.createRef();
    this.state = {
      // tickerInputRef: this.tickerInputRef,
      price: 0,
      ticker: "AMZN",
      tickerInput: "AMZN",
      isLoading: true,
      tickerDDVisible: false,
      tickerDDReservoir: StockData,
      tickerDDFiltered: null
    };
    this.handleKeyUp = this.handleKeyUp.bind(this);

    // O_ZSRWosEBhztl68tl2vPw  us funda
  }
  getTickerURL = ticker =>
    `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${ticker}&interval=5min&outputsize=full&apikey=NKT90SOYWKFBP04F`;

  fetchTicker(ticker) {
    return fetch(this.getTickerURL(ticker))
      .then(res => {
        // console.log(res);
        if (!res.ok) {
          throw Error(
            "sadface, the res doesnt want to be our friend, but dont feel bad.  btw im middleware"
          );
        }
        // console.log(res);
        return res;
      })
      .then(res =>
        // this is the promise so don't have a return statement, it will be if and when  its ready
        // better with es6
        // console.log(res.json())
        res.json()
      );
    // .then(res => enzo = res);
  }
  resolveFetchedTicker(ticker, loadingBool) {
    this.setState({
      isLoading: true
    });
    this.fetchTicker(ticker).then(res => {
      // let myDate = new Date();
      // console.log(myDate);
      let myKeys;
      if (loadingBool) {
        if (res !== undefined && res["Time Series (5min)"] !== undefined) {
          myKeys = Object.keys(res["Time Series (5min)"]);
          this.setState({
            isLoading: false,
            ticker: ticker,
            price: res["Time Series (5min)"][myKeys[0]]["1. open"]
          });
        } else {
          this.setState({
            isLoading: false,
            ticker: "Not a Ticker",
            price: null
          });
        }
      }
    });
  }
  getInputVal(event) {
    let tickerDDList = allTickers;
    // if (event === '') {
    //   this.tickerSymbol = '';
    //   this.tickerTitle = '';
    // }

    console.log(event.target.value);

    tickerDDList.filter(function(myKey) {
      console.log(event.target.value);

      return myKey.symbol.startsWith(event.target.value.toUpperCase());
    });
  }
  updateInputValue(e) {
    const tickerDDList = allTickers.slice();
    let myResults;
    if (e !== null || e !== "") {
      myResults = tickerDDList.filter(function(myKey) {
        return myKey.symbol.startsWith(e.target.value.toUpperCase());
      });
      console.log(myResults);
      this.setState({
        tickerInput: e.target.value,
        tickerDDFiltered: myResults
      });
    }
  }
  getPrice() {
    const myTicker = this.state.tickerInput;
    this.setState({ ticker: myTicker });
    console.log(myTicker);
    this.resolveFetchedTicker(myTicker, true);
  }
  eraseInput() {
    console.log(this.tickerInputRef);
    this.tickerInputRef.current.focus();
    this.setState({
      tickerInput: "",
      ticker: "",
      price: ""
    });
  }
  handleKeyUp(e) {
    this.setState({
      tickerDDVisible: true
    });
  }
  componentDidMount() {
    this._isMounted = true;
    this.resolveFetchedTicker("AMZN", this._isMounted);
    console.log(this.state);
    // console.log(allTickers);
    // this.loadingFunc(true);
  }

  componentWillUnmount() {
    this._isMounted = false;
  }
  render() {
    let price = Math.round(this.state.price * 100) / 100;
    price = this.state.price !== "" ? "$" + price : "";
    const ticker = this.state.ticker;
    // this is a promise
    const filteredTickers = this.state.tickerDDFiltered.map((val, i) => {
      return <li key={i}>{val}</li>;
    });
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: data.bkgd }}
      >
        <IsLoading isLoading={this.state.isLoading}></IsLoading>
        <div className="text-section">
          <h1 className="title">{data.title}</h1>
          <h4 className="subtitle">{data.subtitle}</h4>
        </div>
        <div>
          <div className="flex-row mt-3 crb-title-row">
            <div className="ticker-title">{ticker.toUpperCase()}</div>
            <div className="price-title">{price}</div>
          </div>

          <div className="input-group">
            <div className="relative">
              <div className="clearable">
                <input
                  ref={this.tickerInputRef}
                  className="input-style crb-input"
                  type="text"
                  placeholder="ticker"
                  name="ticker"
                  value={this.state.tickerInput}
                  onChange={e => {
                    this.updateInputValue(e);
                  }}
                  onKeyUp={e => {
                    this.handleKeyUp(e);
                  }}
                />

                {this.state.tickerInput !== "" ? (
                  <span
                    className="clearable-clear mdi mdi-close"
                    onClick={() => {
                      this.eraseInput();
                    }}
                  ></span>
                ) : null}
                {this.state.tickerDDVisible && (
                  <div className="dropdown-container">
                    {/* if bool present */}
                    {/* <ul>
                      {this.state.tickerDDFiltered !== "" &&
                        this.state.tickerDDFiltered.map(val => {
                          <li>{val.symbol}</li>
                        })}
                    </ul> */}
                    <ul>{filteredTickers}</ul>
                  </div>
                )}
              </div>
            </div>
          </div>
          <button
            onClick={() => {
              this.getPrice();
            }}
            className="var(--crb-button-type) large green mt-3 crb-button"
          >
            price
          </button>
        </div>
      </article>
    );
  }
}

export default CRBCard;
