import React from "react";
import * as Common from "../reusable/common";
const imgInsightAnalytics = require("../../assets/images/drilldown_client-thumb.png");
const data = {
  id: 1,
  img: imgInsightAnalytics,
  title: "Insight Analytics",
  subtitle: "A Client Data Dashboard",
  blurb: [
    "Insight Analytics new containerized desktop application within the harbor framework.  It will provide analytical and reporting capabilities to Research Sales, Sales Traders and COO/Operations/Finance/Capital Markets personas.",
    "To showcase a user's client universe as relates to top buys / sells, client value, monthly CV totals with YOY comparison, CV by product, sector totals, percent change, sectors by client type. To focus or drilldown on these specific attributes--as well as readership, touchpoint and 13F data–as they relate to a client / ticker / sector "
  ],
  bkgd: "var(--analytics-bkgd)"
};
const backupPlan = [
  {
    "Rank A: Real-Time Performance": {
      "Consumer Discretionary": "1.57%",
      "Communication Services": "1.43%",
      Energy: "1.31%",
      Materials: "1.30%",
      Industrials: "1.16%",
      "Information Technology": "1.06%",
      Financials: "0.77%",
      "Real Estate": "0.22%",
      "Health Care": "-0.33%",
      "Consumer Staples": "-0.48%",
      Utilities: "-0.88%"
    }
  }
];

const sectorUrl =
  "https://www.alphavantage.co/query?function=SECTOR&apikey=NKT90SOYWKFBP04F";
// const backupPlan = [
//   {
//     "Rank A: Real-Time Performance": {
//       "Consumer Discretionary": "1.57%",
//       "Consumer Staples": "-0.48%",

//       Energy: "1.31%",
//       "Materials": "1.30%",
//       "Industrials": "1.16%",
//       "Information Technology": "1.06%",
//       "Financials": "0.77%",
//       "Real Estate": "0.22%",
//       "Communication Services": "1.43%",
//       "Health Care": "-0.33%",

//       Utilities: "-0.88%"
//     }
//   }
// ];
class InsightAnalyticsCard extends React.Component {
  constructor(props) {
    super(props);
    console.log(
      backupPlan[0]["Rank A: Real-Time Performance"]["Consumer Discretionary"]
    );
    this.state = {
      isLoading: false
    };
    this.resolveFetchedSectors = this.resolveFetchedSectors.bind(this);
    // this.sortStuff();  the sort algo
  }
  sortStuff() {
    let myVal = Object.entries(
      backupPlan[0]["Rank A: Real-Time Performance"]
    ).map(res => {
      return res[1];
    });
    let yellow = Common.mergeSort(myVal);
    console.log(yellow);
  }
  fetchSectors() {
    return fetch(sectorUrl)
      .then(res => {
        if (!res.ok) {
          // throw Error ('sadface');
          return backupPlan;
        }
        return res;
      })
      .then(res => res.json());
  }
  resolveFetchedSectors() {
    this.setState({
      isLoading: true
    });
    this.fetchSectors().then(res => {
      if (
        res !== undefined &&
        res["Rank A: Real-Time Performance"] !== undefined
      ) {
        console.log(res);
        this.setState({
          isLoading: false
        });
      }
    });
  }
  mapObject(res) {
    let tempArr = [];
    Object.entries(res).map((res, i) => {
      let temp = res[1].replace(/\%/, "");
      temp = parseFloat(temp);
      let isNegative = temp < 0 ? true : false;
      tempArr.push({
        name: res[0],
        val: temp,
        isNegative: isNegative
      });
    });
    return tempArr;
  }
  mapSectorBackgrounds(value) {
    if (value === "Communication Services") {
      return "var(--comm-services)";
    } else if (value === "Consumer Discretionary") {
      return "var(--con-discretionary)";
    } else if (value === "Consumer Staples") {
      return "var(--con-staples)";
    } else if (value === "Energy") {
      return "var(--energy)";
    } else if (value === "Financials") {
      return "var(--financials)";
    } else if (value === "Health Care") {
      return "var(--healthcare)";
    } else if (value === "Industrials") {
      return "var(--industrials)";
    } else if (value === "Information Technology") {
      return "var(--info-tech)";
    } else if (value === "Materials") {
      return "var(--materials)";
    } else if (value === "Real Estate") {
      return "var(--real-estate)";
    } else if (value === "Utilities") {
      return "var(--utilities)";
    }
  }

  addWidth(res) {
    let newObject = this.mapObject(res);
    // since it's already sorted, just use 0 as max
    let max = newObject[0].val;
    let finalObject = newObject.map(item => {
      let width = Math.abs(Math.round((item.val / max) * 1000) / 10);
      let color = this.mapSectorBackgrounds(item.name);
      return {
        name: item.name,
        val: item.val,
        isNegative: item.isNegative,
        width: width,
        color: color
      };
    });
    // console.log(finalObject);
    return finalObject;
  }
  componentDidMount() {
    this.resolveFetchedSectors();
  }
  render() {
    // const myObject = Object.entries(
    //   backupPlan["Rank A: Real-Time Performance"]
    // );
    // console.log(Object.entries(backupPlan[0]["Rank A: Real-Time Performance"]));
    const myObject = backupPlan[0]["Rank A: Real-Time Performance"][0];
    const newObject = this.addWidth(
      backupPlan[0]["Rank A: Real-Time Performance"]
    );
    // console.log(newObject);

    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: data.bkgd }}
      >
        <div className="text-section">
          <h1 className="title">{data.title}</h1>
          <h4 className="subtitle">{data.subtitle}</h4>
        </div>
        <div className="flex-row">
          <div className="bar-graph-wrapper">
            <div className="bar-graph">
              {/* {myObject} */}
              {newObject.map((res, i) => {
                if (res.val >= 0) {
                  return (
                    <div
                      key={i}
                      className="flex-row-center narrow-row-height panel-row"
                    >
                      <div className="colA"></div>
                      <div className="colB">
                        <div
                          className="proportion-bar d-flex relative"
                          style={{
                            width: res.width + "%",
                            backgroundColor: res.color
                          }}
                        ></div>
                        <div className="percent-class right-absolute">
                          {res.val}%
                        </div>
                        <div className="flex1"></div>
                      </div>
                    </div>
                  );
                } else {
                  return (
                    <div
                      key={i}
                      className="flex-row-center narrow-row-height panel-row"
                    >
                      <div className="colA">
                        <div
                          className="proportion-bar d-flex relative"
                          style={{
                            width: res.width + "%",
                            backgroundColor: res.color
                          }}
                        ></div>
                        <div className="percent-class left-absolute">
                          {res.val}%
                        </div>
                        <div className="flex1"></div>
                      </div>
                      <div className="colB"></div>
                    </div>
                  );
                }
              })}
            </div>
          </div>

          <div class="legend">
            {newObject.map((res, i) => {
              return (
                <div
                  key={`legend-${i}`}
                  className="flex-row-center narrow-row-height"
                >
                  <div
                    className="square"
                    style={{
                      backgroundColor: res.color
                    }}
                  ></div>
                  <div className="title cursor-pointer">{res.name}</div>
                </div>
              );
            })}
          </div>
        </div>
      </article>
    );
  }
}

export default InsightAnalyticsCard;
