import React from "react";
import todoData from "../../data/todoData";
import TodoItem from "./todoItem";
/**
 * Let's make it so our checkbox can actually mark our todo as complete or incomplete!
 * This challenge is a little more involved than some of the past ones. Check the comments
 * in the code for some help on accomplishing this one
 *
 * Challenge:
 * 1. Create an event handler in the App component for when the checkbox is clicked (which is an `onChange` event)
 *    a. This method will be the trickest part. Check the comments in the stubbed-out method below for some pseudocode to help guide you through this part
 * 2. Pass the method down to the TodoItem component
 * 3. In the TodoItem component, make it so when the `onChange` event happens, it calls the `handleChange` method and passes the id of the todo into the function
 */
class ToDoCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: todoData
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(id) {
    this.setState(prevState => {
      let myNewItems = prevState.todos.map(val => {
        if (val.id === id) {
          console.log("before val", val);
          let imNotAlteringState = { ...val };
          imNotAlteringState.completed = !imNotAlteringState.completed;
          console.log("after val", val);
          return imNotAlteringState;
        }
        // somehow it wont function without this return
        return val;
      });
      return { todos: myNewItems };
    });
    // Update state so that the item with the given id flips `completed` from false to true (or vise versa)
    // Remember not to modify prevState directly, but instead to return a new version of state with the change you want included in that update. (Think how you might use the `.map` method to do this)
  }
  render() {
    return (
      <div>
        {this.state.todos.map((item, i) => {
          return (
            <TodoItem
              key={i}
              id={item.id}
              attributes={item}
              handleChange={this.handleChange}
            ></TodoItem>
          );
        })}
      </div>
    );
  }
}
export default ToDoCard;
