import React from "react";

const imgCRB = require("../../images/crb-img.png");
const data = {
  id: 3,
  img: imgCRB,
  title: "CRB Pricer",
  subtitle: "Central Risk Book Pricing and Trade Execution",
  blurb: [""],
  bkgd: "var(--crb-bkgd)"
};
class CRBCard extends React.Component {
  constructor() {
    super();
    this.state = {
      price: 0,
      ticker: this.ticker
    };
    this.setPrice = this.setPrice.bind(this);
    console.log("i am AMZN", this.getTickerURL("AMZN"));
  }
  getTickerURL = ticker =>
    `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${ticker}&interval=1min&outputsize=full&apikey=NKT90SOYWKFBP04F`;

  setPrice() {
    this.setState(prevState => {
      return {
        price: prevState.price + 1
      };
    });
  }
  render() {
    const price = this.state.price;

    fetch(this.getTickerURL("AMZN"))
      .then(res => {
        console.log(res);
        if (!res.ok) {
          throw Error(
            "sadface, the res doesnt want to be our friend, but dont feel bad"
          );
        }
        return res;
      })
      .then(res => res.json())
      .then(res => console.log(res));
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: data.bkgd }}
      >
        <div className="text-section">
          <h1 className="title">{data.title}</h1>
          <h4 className="subtitle">{data.subtitle}</h4>
        </div>
        <div>
          <button onClick={this.setPrice} className="outline-btn accent mt-3">
            price
          </button>
          {price}
        </div>
        {/* {props.data.divs} */}
        {/* <div
          style={{ backgroundColor: "yellow", width: "50%", height: "15px" }}
        ></div> */}
        {/* <div class="blurb-thumb">
          <p className="blurb">{props.data.blurb}</p>
          <div className="thumb">
            <img src={props.data.img} alt={props.data.subtitle} />
          </div>
        </div> */}
      </article>
    );
  }
}

export default CRBCard;
