/* eslint-disable no-unused-vars */
import React, { useEffect } from "react";
import Game from "./components/game/game";

import "./App.scss";
import Nav from "./components/nav";
import { DARK } from "./global-styles/theme/dark";
import { LIGHT } from "./global-styles/theme/light";
import ToDoCard from "./components/learning/ToDoCard";
import PortfolioCard from "./components/portfolio-card";
import Footer from "./components/footer";
import portfolioData from "./data/portfolioData";
import AlertingCard from "./components/alerting/AlertingCard";
import AlphaCapCard from "./components/alphaCapture/AlphaCapCard";
import InsightAnalyticsCard from "./components/insightAnalytics/InsightAnalyticsCard";
import CRBCard from "./components/crb/CRBCard";
// https://reactjs.org/docs/faq-functions.html#how-can-i-prevent-a-function-from-being-called-too-quickly-or-too-many-times-in-a-row
import rafSchedule from "raf-schd";
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/filter
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/map
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/sort
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/reduce
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/every
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/some
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/find
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/findindex
// https://scrimba.com/p/p4Mrt9/cQnMDHD  js Class
// // https://reactjs.org/docs/events.html#supported-events
// why make a class instead of a function?  to use lifecycle functions

class App extends React.Component {
  // defaultTheme = "light";
  dark = DARK;
  light = LIGHT;

  constructor(props) {
    super(props);

    this.bodyRef = React.createRef();
    this.alertingRef = React.createRef();
    this.changeMeRef = React.createRef();
    // not sure why to add the below
    this.handleScroll = this.handleScroll.bind(this);
    this.handleResize = this.handleResize.bind(this);
    this.handleClassAddingOnScroll = this.handleClassAddingOnScroll.bind(this);
    this.setScrollingConstants = this.setScrollingConstants.bind(this);
    // this.scheduleUpdate = rafSchedule(point =>
    //   this.state.myAlerting.onScroll(point)
    // );
    const schedule = rafSchedule(this.handleScroll);
    // according to react docs, deriving state from props is bad
    // https://reactjs.org/docs/react-component.html#getsnapshotbeforeupdate
    this.state = {
      theme: props.theme,
      myBody: this.bodyRef,
      myChangeMe: this.changeMeRef,
      myAlerting: this.alertingRef,
      scrollHeight: null,
      clientHeight: null,
      bodyOffset: null,
      visibleArea: null,
      scrollAmount: null,
      alertingOffset: null
    };

    // this.setTheme(props.theme);
    if (props.theme === "light") {
      // Object.assign(this.light.properties, this.appLight.properties);
      this.generateVars(this.light);
    } else {
      // Object.assign(this.dark.properties, this.appDark.properties);
      this.generateVars(this.dark);
    }
    this.setTheme = this.setTheme.bind(this);

    // console.log(props.theme, this.state.theme);
    console.log(this.bodyRef);
    console.log(this.alertingRef);
    // this.generateTheme(props.theme); //  LOOK HERE

    // this.getActiveTheme = this.getActiveTheme.bind(this);
  }

  generateVars(theme) {
    Object.keys(theme.properties).forEach(property => {
      document.documentElement.style.setProperty(
        property,
        theme.properties[property]
      );
    });
  }
  generateTheme(theme) {
    const current = theme;
    // console.log("i am active theme", theme);
    if (theme === "light") {
      // Object.assign(this.light.properties, this.appLight.properties);
      this.generateVars(current);
    } else {
      // Object.assign(this.dark.properties, this.appDark.properties);
      this.generateVars(current);
    }
  }
  setTheme() {
    // const theme = this.state.theme === "light" ? "dark" : "light";
    const theme = this.state.theme === "light" ? "dark" : "light";

    // console.log(theme);

    localStorage.setItem("theme", theme);
    // this.document.querySelector("html").className = "theme-" + theme;
    if (theme === "light") {
      console.log("yo, im light");
      this.setState({ theme: "light" });
      // this.state.theme = "light";
      console.log(this.state);
      this.generateTheme(this.light);
    } else {
      console.log("yo, im dark");
      this.setState({ theme: "dark" });
      // this.state.theme = "dark";
      console.log(this.state);
      this.generateTheme(this.dark);
    }
  }
  setChangeClass(myRef, myClass, myTimeout, scrollAmount) {
    const changeMe = myRef;
    const currentScroll = this.state.state.scrollAmount;
    // this just adds the class no matter what
    changeMe.current.classList.add(myClass);
    console.log("i am current scroll", currentScroll);
    // test if the current has been the same for a long while
    setTimeout(() => {
      if (currentScroll === this.state.scrollAmount) {
        changeMe.current.classList.remove(myClass);
      }
      console.log("setTimeout");
    }, myTimeout);
  }
  handleClassAddingOnScroll(
    alertingOffset,
    visibleArea,
    scrollAmount,
    myRef,
    myClass,
    myTimeout
  ) {
    // this tests if the thing is in view
    if (alertingOffset - visibleArea > scrollAmount) {
      console.log("i am equal");
      this.setChangeClass(myRef, myClass, myTimeout, scrollAmount);
    }
    // do something with the scroll position
  }

  handleScroll(e) {
    // https://reactjs.org/docs/faq-functions.html#how-can-i-prevent-a-function-from-being-called-too-quickly-or-too-many-times-in-a-row
    // When we receive a scroll event, schedule an update.
    // If we receive many updates within a frame, we'll only publish the latest value.
    // this.scheduleUpdate({ x: e.clientX, y: e.clientY });

    const scrollAmount = this.state.myBody.current.scrollTop;
    const alertingOffset = this.state.alertingOffset;
    const visibleArea = this.state.visibleArea;
    this.setState({
      scrollAmount: scrollAmount
    });
    window.requestAnimationFrame(() => {
      this.handleClassAddingOnScroll(
        alertingOffset,
        visibleArea,
        scrollAmount,
        this.state.myAlerting,
        "blinkDivUp",
        5000
      );
      this.setChangeClass(this.state.myChangeMe, "blinkDivUpPlain", 1000);
      console.log("i am inside the RAF function");
      // ticking = false;
    });
    // this.handleClassAddingOnScroll(
    //   alertingOffset,
    //   visibleArea,
    //   scrollAmount,
    //   this.state.myAlerting,
    //   "blinkDivUp",
    //   5000
    // );

    console.log(this.state);
  }

  setScrollingConstants() {
    const scrollHeight = this.state.myBody.current.scrollHeight;
    const clientHeight = this.state.myBody.current.clientHeight;
    const bodyOffset = this.state.myBody.current.offsetTop;
    const visibleArea =
      this.state.myBody.current.clientHeight +
      this.state.myBody.current.offsetTop;
    const scrollAmount = this.state.myBody.current.scrollTop;
    const alertingOffset = this.state.myAlerting.current.offsetTop;

    this.setState({
      scrollHeight: scrollHeight,
      clientHeight: clientHeight,
      bodyOffset: bodyOffset,
      visibleArea: visibleArea,
      scrollAmount: scrollAmount,
      alertingOffset: alertingOffset
    });
  }
  handleResize() {
    // https://reactjs.org/docs/faq-functions.html#how-can-i-prevent-a-function-from-being-called-too-quickly-or-too-many-times-in-a-row
    // When we receive a scroll event, schedule an update.
    // If we receive many updates within a frame, we'll only publish the latest value.
    // this.scheduleUpdate({ x: e.clientX, y: e.clientY });
    this.setScrollingConstants();
    console.log(this.state);
    console.log("i resized");
  }
  componentDidMount() {
    this.setScrollingConstants();

    window.addEventListener("resize", this.handleResize.bind(this));
  }
  componentWillUnmount() {
    // Cancel any pending updates since we're unmounting.
    this.scheduleUpdate.cancel();
    console.log("componentDidUnmount");
    window.removeEventListener("resize", this.handleResize.bind(this));
  }
  render() {
    // display logic goes here
    // never set a variable or call a function in the render function unless absolutely necessary
    const status = this.state.theme;
    console.log("i am pd", portfolioData);
    const scrollHeight = this.state.scrollHeight;
    const clientHeight = this.state.clientHeight;

    const bodyOffset = this.state.bodyOffset;
    const visibleArea = this.state.visibleArea;
    const scrollAmount = this.state.scrollAmount;
    const alertingOffset = this.state.alertingOffset;

    // setTimeout(() => {
    //   console.log(constants.scrollHeight);
    // }, 3000);
    // console.log(scrollHeight);
    return (
      <div className={"theme-" + status}>
        <Nav getTheme={this.getActiveTheme} setTheme={this.setTheme} />
        <div
          className="inner-body"
          onScroll={this.handleScroll}
          ref={this.bodyRef}
        >
          <div className="headline">
            Hi, I'm Amanda Innis, a designer &amp; creative technologist
          </div>

          <div className="card-wrapper">
            <div className="wrapper-padding constants-info">
              <ul>
                <li>
                  window to wrapper
                  <span>{bodyOffset}</span>
                </li>
                <li>
                  height of wrapper
                  <span>{clientHeight}</span>
                </li>

                <li>
                  height visible
                  <span>{visibleArea}</span>
