import React from "react";
class Nav extends React.Component {
  toggleTheme(theme) {
    this.props.setTheme(theme === "light" ? "dark" : "light");
  }

  componentDidMount() {}
  render() {
    return (
      <div className="navbar">
        <div
          className="nav-link"
          onClick={() => {
            this.props.poMansRouting("home");
          }}
        >
          BananaPhone
        </div>
        <div
          className="nav-link"
          onClick={() => {
            this.props.poMansRouting("dataViz");
          }}
        >
          DataViz
        </div>
        <div
          className="nav-link"
          onClick={() => {
            this.props.poMansRouting("portfolio");
          }}
        >
          Portfolio
        </div>
        <button
          className="sunlight-nighttime ml-auto"
          onClick={() => this.toggleTheme(this.props.myTheme)}
        >
          <div className="sun"></div> <div className="night"></div>
          Theme
        </button>
      </div>
    );
  }
}

export default Nav;
