import React from 'react';
import Square from './square';


class Board extends React.Component {
  constructor(props) {
    super(props);
    const myArr = Array(9).fill(null).slice();
    this.state = {
      squares: myArr,
      xIsNext: true
    }
  }
  handleMyClick(i){
    // console.log('handleclcik', bleh);
    const mySquares = this.state.squares.slice();
    mySquares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      squares: mySquares,
      xIsNext: !this.state.xIsNext,
    });
  }
  renderSquare(i) {
    console.log(i);
    console.log('i am state i ', this.state);
    return ( <Square value={this.state.squares[i]}  myClick={() => this.handleMyClick(i)}/>);
  }

  render() {
    const status = `Next player: ${this.state.xIsNext ? 'X' : 'O'}`;

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

export default Board;
