import React from "react";
import Checkbox from "./Checkbox";

let formData;
const memeApi = "https://api.imgflip.com/get_memes";
class MemeGenerator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allMemeImgs: [],
      selectedMemes: [],
      randomImg: "https://i.imgflip.com/345v97.jpg",
      topText: "",
      bottomText: ""
    };

    this.handleChange = this.handleChange.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleMemes = this.handleMemes.bind(this);
    this.getAllMemes = this.getAllMemes.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
    // console.log(this.state);
  }
  handleSubmit() {}
  getAllMemes(memes) {
    this.setState({
      allMemeImgs: memes,
      randomImg: memes[0]
    });
    console.log(this.state);
    console.log(this.state.allMemeImgs[0]);
  }
  handleMemes(memes) {
    this.setState(prevState => {
      console.log(prevState);
      return {
        selectedMemes: memes[0]
      };
    });
  }
  componentDidMount() {
    fetch(memeApi)
      .then(res => res.json())
      .then(res => {
        console.log(res.data.memes);

        this.getAllMemes(res.data.memes);
      });
  }
  render() {
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: "var(--background-main)" }}
      >
        <div className="text-section">
          <h1 className="title">Memes</h1>
        </div>
        {/* <form onSubmit={() => this.handleSubmit()}> */}

        <div className="mt-2">
          <div className="input-group mt-2">
            <div className="label-style">Top Text</div>
            <input
              className="input-style"
              name="topText"
              value={this.state.topText}
              type="text"
              onChange={e => this.handleChange(e)}
            />
          </div>
          <div className="input-group mt-2">
            <div className="label-style">Bottom Text</div>
            <input
              className="input-style"
              name="bottomText"
              value={this.state.bottomText}
              type="text"
              onChange={e => this.handleChange(e)}
            />
          </div>
          <div
            className="meme-div"
            style={{ backgroundImage: `url(${this.state.randomImg["url"]})` }}
          >
            <div className="meme-text mt-2">{this.state.topText}</div>
            <div className="meme-text">{this.state.bottomText}</div>
          </div>
          <small class="mt-1"> {this.state.randomImg["name"]}</small>
        </div>
        <div>
          <button
            className="outline-btn accent"
            onClick={() => {
              this.handleSubmit();
            }}
          >
            submit
          </button>
        </div>
        {/* </form> */}
      </article>
    );
  }
}

export default MemeGenerator;
