import React from "react";
import IsLoading from "../reusable/IsLoading";
import * as Common from "../reusable/common";
const imgCRB = require("../../assets/images/crb-img.png");
const data = {
  id: 3,
  img: imgCRB,
  title: "CRB Pricer",
  subtitle: "Central Risk Book Pricing and Trade Execution",
  blurb: [""],
  bkgd: "var(--crb-bkgd)"
};
class CRBCard extends React.Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.tickerInputRef = React.createRef();
    this.state = {
      // tickerInputRef: this.tickerInputRef,
      price: 0,
      ticker: "AMZN",
      tickerInput: "AMZN",
      isLoading: true
    };
    // this.loadingFunc = this.loadingFunc.bind(this);
    this.setPrice = this.setPrice.bind(this);
    console.log("i am AMZN", this.getTickerURL("AMZN"));
  }
  getTickerURL = ticker =>
    `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${ticker}&interval=5min&outputsize=full&apikey=NKT90SOYWKFBP04F`;

  setPrice() {
    this.setState(prevState => {
      return {
        price: prevState.price + 1
      };
    });
  }

  fetchTicker(ticker) {
    return fetch(this.getTickerURL(ticker))
      .then(res => {
        // console.log(res);
        if (!res.ok) {
          throw Error(
            "sadface, the res doesnt want to be our friend, but dont feel bad.  btw im middleware"
          );
        }
        // console.log(res);
        return res;
      })
      .then(res =>
        // this is the promise so don't have a return statement, it will be if and when  its ready
        // better with es6
        // console.log(res.json())
        res.json()
      );
    // .then(res => enzo = res);
  }
  resolveFetchedTicker(ticker, loadingBool) {
    this.setState({
      isLoading: true
    });
    this.fetchTicker(ticker).then(res => {
      // let myDate = new Date();
      // console.log(myDate);
      let myKeys;
      if (loadingBool) {
        if (res !== undefined && res["Time Series (5min)"] !== undefined) {
          myKeys = Object.keys(res["Time Series (5min)"]);
          // console.log(
          //   "woof",
          //   // res["Time Series (5min)"],
          //   myKeys[myKeys.length - 1],
          //   myKeys[0],
          //   myKeys
          // );

          this.setState({
            isLoading: false,
            ticker: ticker,
            price: res["Time Series (5min)"][myKeys[0]]["1. open"]
          });
        } else {
          this.setState({
            isLoading: false,
            ticker: "Not a Ticker",
            price: null
          });
        }
      }
    });
  }
  // loadingFunc(bool) {
  //   let myLovelyTimeout;
  //   if (bool) {
  //     myLovelyTimeout = setTimeout(() => {
  //       this.setState({
  //         isLoading: false
  //       });
  //     }, 1500);
  //   } else {
  //     clearTimeout(myLovelyTimeout);
  //   }
  // }
  updateInputValue(e) {
    this.setState({ tickerInput: e.target.value.toUpperCase() });
  }
  getPrice() {
    const myTicker = this.state.tickerInput;
    this.setState({ ticker: myTicker });
    console.log(myTicker);
    this.resolveFetchedTicker(myTicker, true);
  }
  eraseInput() {
    console.log(this.tickerInputRef);
    this.tickerInputRef.current.focus();
    this.setState({
      tickerInput: "",
      ticker: "",
      price: ""
    });
  }
  componentDidMount() {
    this._isMounted = true;
    this.resolveFetchedTicker("AMZN", this._isMounted);

    // // console.log(myTicker);
    // this.loadingFunc(true);
  }

  componentWillUnmount() {
    this._isMounted = false;
  }
  render() {
    let price = Math.round(this.state.price * 100) / 100;
    price = this.state.price !== "" ? "$" + price : "";
    const ticker = this.state.ticker;
    // this is a promise

    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: data.bkgd }}
      >
        <IsLoading isLoading={this.state.isLoading}></IsLoading>
        <div className="text-section">
          <h1 className="title">{data.title}</h1>
          <h4 className="subtitle">{data.subtitle}</h4>
        </div>
        <div>
          <div className="flex-row mt-3 crb-title-row">
            <div className="ticker-title">{ticker}</div>
            <div className="price-title">{price}</div>
          </div>
          <div className="input-group">
            <div className="clearable">
              <input
                ref={this.tickerInputRef}
                className="input-style crb-input"
                type="text"
                placeholder="ticker"
                value={this.state.tickerInput}
                onChange={this.updateInputValue}
              />

              {this.state.tickerInput !== "" ? (
                <span
                  className="clearable-clear mdi mdi-close"
                  onClick={() => {
                    this.eraseInput();
                  }}
                ></span>
              ) : null}
            </div>
          </div>
          <button
            onClick={() => {
              this.getPrice();
            }}
            className="var(--crb-button-type) large green mt-3 crb-button"
          >
            price
          </button>
        </div>

        {/* {props.data.divs} */}
        {/* <div
          style={{ backgroundColor: "yellow", width: "50%", height: "15px" }}
        ></div> */}
        {/* <div class="blurb-thumb">
          <p className="blurb">{props.data.blurb}</p>
          <div className="thumb">
            <img src={props.data.img} alt={props.data.subtitle} />
          </div>
        </div> */}
      </article>
    );
  }
}

export default CRBCard;
