import React from "react";
import Game from "./components/game";
import "./App.scss";
import Nav from "./components/nav";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: "light"
      // activeArticleId: 1
    };
    this.setTheme = this.setTheme.bind(this);
  }
  // toggleTheme() {
  //   const theme = this.state.theme === "light" ? "dark" : "light";
  //   console.log(theme);
  //   // document.documentElement.classList.add("color-theme-in-transition");
  //   this.setState({ theme });
  //   document.documentElement.setAttribute("data-theme", theme);
  //   // window.setTimeout(() => {
  //   //   document.documentElement.classList.remove("color-theme-in-transition");
  //   // }, 1000);
  // }
  setTheme() {
    // this.theme$.next(theme);
    const theme = this.state.theme === "light" ? "dark" : "light";
    console.log(theme);
    console.log(this.document.querySelector("html"));
    // document.documentElement.classList.add("color-theme-in-transition");
    this.setState({ theme });
    localStorage.setItem("theme", theme);
    this.document.querySelector("html").className = "theme-" + theme;
    // if (theme === "light") {
    //   this.generateTheme(this.light);
    // } else {
    //   this.generateTheme(this.dark);
    // }
  }
  render() {
    // const { activeArticleId } = this.state;
    return (
      <div className="app">
        <Nav setTheme={this.setTheme} />

        <h1>Board Game</h1>
        <Game />
      </div>
    );
  }

  // return (
  //   <div className="blah">

  //   </div>
  // );
}

export default App;
