import React, { useState, useRef, useEffect, createRef } from "react";
import * as DataHandler from "../../data/DataHandler";
import LineChart from "../reusable/LineChart";
import CardFooter from "../reusable/Card-Footer";
const imgAlphaCap = require("../../assets/images/alphaCapture.png");
const data = {
  id: 2,
  img: imgAlphaCap,
  title: "Alpha Capture",
  subtitle: "A Trade Idea Tracker",
  blurb: [""],
  bkgd: "var(--alphaCap-bkgd)"
};
function AlphaCapCard(props) {
  const [cardVisible, setCardVisible] = useState(true);

  console.log(props);
  function percentFormatFn(d) {
    return Math.round(d * 10000) / 100 + "%";
  }
  function viewRole() {
    if (cardVisible) {
      setCardVisible(false);
    } else {
      setCardVisible(true);
    }
  }
  return (
    <article
      className="portfolio-card alpha-capture"
      style={{ backgroundColor: data.bkgd }}
    >
      {cardVisible && (
        <div className="inner-wrapper">
          <div className="text-section">
            <h1 className="title">{data.title}</h1>
            <h4 className="subtitle">{data.subtitle}</h4>
          </div>
          <LineChart
            data={DataHandler.alphaCaptureChartData}
            valueArray={[
              {
                dataKey: "pricePercent",
                color: "#7f5ada",
                marker: "#a07fef",
                tooltipLabel: "Price %"
              },
              {
                dataKey: "benchmarkPercent",
                color: "#0da584", //"#93bb96"
                marker: "#10d4a9", //"#aadeae"
                tooltipLabel: "Benchmark %"
              }
            ]}
            myID={"alphaCap"}
            numberFormatFn={percentFormatFn}
            resize={props.resize}
          />
        </div>
      )}
      {!cardVisible && (
        <div className="inner-wrapper">
          <div className="text-section">
            <h1 className="title">My Role</h1>
          </div>

          <div className="role">
            <div className="title">BofA Application (2018 onward)</div>
            <ul className="disc">
              <li>Analysis, interviews, requirements</li>
              <li>Wireframes, hi-fi designs</li>
              <li>Angular prototype and production code</li>
              <li>Highcharts customization</li>

              <li>All SCSS code and theming</li>
            </ul>
          </div>

          <div className="role">
            <div className="title">This Prototype</div>
            <ul className="disc">
              <li>React, D3.js, SCSS</li>
            </ul>
          </div>
        </div>
      )}
      <CardFooter roleBool={cardVisible} viewRole={viewRole} />
    </article>
  );
}

export default AlphaCapCard;
