import React, { useState } from "react";

function MemeGenerator() {
  let memeDDConst;
  this.memeInputRef = React.createRef();
  this.ddRef = React.createRef();
  const memeApi = "https://api.imgflip.com/get_memes";

  const [allMemeImgs] = useState([]);
  const [memeDDFiltered] = useState([]);
  const [memeImg] = useState({});
  const [topText] = useState("");
  const [bottomText] = useState("");
  const [memeDDVisible] = useState(false);
  const [savedMemes] = useState([]);

  // constructor(props) {
  //   super(props);

  //   this.state = {
  //     allMemeImgs: [],
  //     memeDDFiltered: [],
  //     memeImg: {},
  //     topText: "",
  //     bottomText: "",
  //     memeInput: "",
  //     memeDDVisible: false,
  //     savedMemes: []
  //   };

  //   this.handleSubmit = this.handleSubmit.bind(this);
  //   this.saveMemes = this.saveMemes.bind(this);
  //   this.getAllMemes = this.getAllMemes.bind(this);
  // }

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
    // console.log(this.state);
  };

  handleMemeInput = e => {
    let myResults;
    // this.setState({
    //   memeInput: e.target.value
    // });
    console.log(memeDDConst);
    if (e !== null || e !== "") {
      myResults = memeDDConst.filter(function(myKey) {
        return myKey["name"]
          .toLowerCase()
          .startsWith(e.target.value.toLowerCase());
      });
      console.log(myResults);
      this.setState({
        memeInput: e.target.value,
        memeDDFiltered: myResults
      });
      // this.ddRef.current.childNodes[0].focus();
      // console.log(this.ddRef.current);
    }
  };
  eraseInput = () => {
    console.log(this.memeInputRef);
    this.memeInputRef.current.focus();
    this.setState({
      memeInput: "",
      memeDDVisible: true,
      memeDDFiltered: memeDDConst
    });
  };
  //   handleKeyUp(e) {
  //     console.log(e.which);
  //     this.setState({
  //       memeDDVisible: true
  //     });
  //     // if (e.which === 40) {
  //     //   this.ddRef.current.children[this.state.ddListIndex].focus();
  //     // }
  //   }
  handleInputClick = () => {
    this.setState({
      memeDDVisible: true,
      memeDDFiltered: memeDDConst
    });
  };
  handleDDClick = (val, name) => {
    console.log(val);
    this.setState({
      [name]: val,
      memeInput: val.name,
      memeDDVisible: false
    });
  };
  getAllMemes = memes => {
    this.setState({
      allMemeImgs: memes,
      memeImg: memes[0]
    });
    memeDDConst = memes;
    // console.log(this.state);
    // console.log(this.state.allMemeImgs[0]);
  };
  handleSubmit = e => {
    e.preventDefault();
    // this.setState(prevState => {
    //   formData = prevState.firstName;
    //   return {
    //     firstName: "",
    //     lastName: "",
    //     checkboxData: checkboxData,
    //     radioData: radioData
    //   };
    // });
    // return formData;
  };
  saveMemes = e => {
    e.preventDefault();
    this.setState(prevState => {
      console.log(prevState);
      let newList = { ...prevState.savedMemes };
      console.log(newList);
      //   newList.push({
      //     topText: this.state.topText,
      //     bottomText: this.state.bottomText,
      //     memeImg: this.state.memeImg["url"],
      //     name: this.state.memeImg["name"]
      //   });
      return {
        savedMemes: newList
      };
    });
    // console.log(this.state);
  };
  componentDidMount = () => {
    fetch(memeApi)
      .then(res => res.json())
      .then(res => {
        console.log(res.data.memes);

        this.getAllMemes(res.data.memes);
      });
  };
  componentDidMount();
  return (
    <article
      className="portfolio-card"
      style={{ backgroundColor: "var(--background-main)" }}
    >
      <div className="text-section">
        <h1 className="title">MemeGenerator</h1>
        {/* <h4 className="subtitle">Login</h4> */}
      </div>
      <form onSubmit={e => this.handleSubmit(e)}>
        <div className="input-group">
          <div className="relative">
            <div className="clearable">
              <input
                ref={this.memeInputRef}
                className="input-style"
                type="text"
                placeholder="search meme"
                name="memeInput"
                autoComplete="off"
                value={this.state.memeInput}
                onChange={e => {
                  this.handleMemeInput(e);
                }}
                onClick={() => {
                  this.handleInputClick();
                }}
              />

              {this.state.memeInput !== "" ? (
                <span
                  className="clearable-clear mdi mdi-close"
                  onClick={() => {
                    this.eraseInput();
                  }}
                ></span>
              ) : null}
              {this.state.memeDDVisible && (
                <div className="dropdown-container" ref={this.ddRef}>
                  {this.state.memeDDFiltered !== "" &&
                    this.state.memeDDFiltered.map((val, i) => {
                      return (
                        <button
                          key={i}
                          onClick={e => {
                            this.handleDDClick(val, "memeImg");
                          }}
                          // value={this.state.selectedTicker}
                        >
                          <div
                            className="meme-dd-div"
                            style={{
                              backgroundImage: `url(${val["url"]})`
                            }}
                          ></div>
                          - {val.name}
                        </button>
                      );
                    })}
                </div>
              )}
            </div>
          </div>
        </div>
        <div className="mt-2">
          <div className="input-group mt-2">
            <div className="label-style">Top Text</div>
            <input
              className="input-style"
              name="topText"
              value={this.state.topText}
              type="text"
              autoComplete="off"
              onChange={e => this.handleChange(e)}
            />
          </div>
          <div className="input-group mt-2">
            <div className="label-style">Bottom Text</div>
            <input
              className="input-style"
              name="bottomText"
              value={bottomText}
              type="text"
              autoComplete="off"
              onChange={e => this.handleChange(e)}
            />
          </div>
          <div
            className="meme-div mt-2"
            style={{ backgroundImage: `url(${memeImg["url"]})` }}
          >
            <div className="meme-text">{topText}</div>
            <div className="meme-text">{bottomText}</div>
          </div>
          <small className="mt-1"> {memeImg["name"]}</small>
        </div>
        <div className="mt-2">
          <button className="outline-btn accent">save</button>
        </div>
      </form>
    </article>
  );
}

export default MemeGenerator;
