import React, { useState, useEffect } from "react";

function MemeGenerator() {
  let memeDDConst;
  const memeInputRef = React.createRef();
  const ddRef = React.createRef();
  const memeApi = "https://api.imgflip.com/get_memes";

  const [allMemeImgs, setAllMemeImgs] = useState([]);
  const [memeDDFiltered, setMemeDDFiltered] = useState([]);
  const [memeImg, setMemeImg] = useState({});
  const [memeInput, setMemeInput] = useState("");

  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");
  const [memeDDVisible, setMemeDDVisible] = useState(false);
  const [savedMemes, setSavedMemes] = useState([]);

  function handleMemeInput(e) {
    let myResults;
    // console.log(allMemeImgs);
    let memeDDTemp = { ...memeDDConst };
    console.log(memeDDTemp);
    console.log(e);
    if (e !== null || e !== "") {
      myResults = memeDDTemp.filter(myKey => {
        return myKey["name"]
          .toLowerCase()
          .includes(e.target.value.toLowerCase());
      });
      console.log(myResults);
      setMemeDDFiltered(myResults);
      setMemeInput(e.target.value);

      // this.ddRef.current.childNodes[0].focus();
      // console.log(this.ddRef.current);
    }
  }
  function eraseInput() {
    console.log(memeInputRef);
    memeInputRef.current.focus();
    setMemeDDFiltered(allMemeImgs);
    setMemeInput("");
    setMemeDDVisible(true);
  }
  //   handleKeyUp(e) {
  //     console.log(e.which);
  //     this.setState({
  //       memeDDVisible: true
  //     });
  //     // if (e.which === 40) {
  //     //   this.ddRef.current.children[ddListIndex].focus();
  //     // }
  //   }
  function handleInputClick() {
    setMemeDDFiltered(allMemeImgs);
    setMemeDDVisible(true);
  }
  function handleDDClick(val) {
    console.log(val);
    setMemeDDVisible(false);
    setMemeInput(val.name);
    setMemeImg(val);
  }

  function handleSubmit(e) {
    e.preventDefault();
    // this.setState(prevState => {
    //   formData = prevState.firstName;
    //   return {
    //     firstName: "",
    //     lastName: "",
    //     checkboxData: checkboxData,
    //     radioData: radioData
    //   };
    // });
    // return formData;
  }
  function saveMemes(e) {
    e.preventDefault();
    this.setState(prevState => {
      console.log(prevState);
      let newList = { ...prevState.savedMemes };
      console.log(newList);
      //   newList.push({
      //     topText: topText,
      //     bottomText: bottomText,
      //     memeImg: memeImg["url"],
      //     name: memeImg["name"]
      //   });
      return {
        savedMemes: newList
      };
    });
    // console.log(this.state);
  }

  useEffect(() => {
    fetch(memeApi)
      .then(res => res.json())
      .then(res => {
        console.log(res.data.memes);

        setAllMemeImgs(res.data.memes);
        setMemeImg(res.data.memes[0]);

        memeDDConst = res.data.memes;
        console.log(memeDDConst);
        // console.log(allMemeImgs[0]);
      });
  }, []);
  //
  return (
    <article
      className="portfolio-card"
      style={{ backgroundColor: "var(--background-main)" }}
    >
      <div className="text-section">
        <h1 className="title">MemeGenerator</h1>
        {/* <h4 className="subtitle">Login</h4> */}
      </div>
      <form onSubmit={e => handleSubmit(e)}>
        <div className="input-group">
          <div className="relative">
            <div className="clearable">
              <input
                ref={memeInputRef}
                className="input-style"
                type="text"
                placeholder="search meme"
                name="memeInput"
                autoComplete="off"
                value={memeInput}
                onChange={e => {
                  handleMemeInput(e);
                }}
                onClick={() => {
                  handleInputClick();
                }}
              />

              {memeInput !== "" ? (
                <span
                  className="clearable-clear mdi mdi-close"
                  onClick={() => {
                    eraseInput();
                  }}
                ></span>
              ) : null}
              {memeDDVisible && (
                <div className="dropdown-container" ref={ddRef}>
                  {memeDDFiltered !== "" &&
                    memeDDFiltered.map((val, i) => {
                      return (
                        <button
                          key={i}
                          onClick={e => {
                            handleDDClick(val);
                          }}
                          // value={this.selectedTicker}
                        >
                          <div
                            className="meme-dd-div"
                            style={{
                              backgroundImage: `url(${val["url"]})`
                            }}
                          ></div>
                          - {val.name}
                        </button>
                      );
                    })}
                </div>
              )}
            </div>
          </div>
        </div>
        <div className="mt-2">
          <div className="input-group mt-2">
            <div className="label-style">Top Text</div>
            <input
              className="input-style"
              name="topText"
              value={topText}
              type="text"
              autoComplete="off"
              onChange={e => setTopText(e.target.value)}
            />
          </div>
          <div className="input-group mt-2">
            <div className="label-style">Bottom Text</div>
            <input
              className="input-style"
              name="bottomText"
              value={bottomText}
              type="text"
              autoComplete="off"
              onChange={e => setBottomText(e.target.value)}
            />
          </div>
          <div
            className="meme-div mt-2"
            style={{ backgroundImage: `url(${memeImg["url"]})` }}
          >
            <div className="meme-text">{topText}</div>
            <div className="meme-text">{bottomText}</div>
          </div>
          <small className="mt-1"> {memeImg["name"]}</small>
        </div>
        <div className="mt-2">
          <button className="outline-btn accent">save</button>
        </div>
      </form>
    </article>
  );
}

export default MemeGenerator;
