import React from "react";
const checkboxData = [
  { id: "c1", name: "tickers", label: "Tickers", checked: false },
  { id: "c2", name: "dogs", label: "Dogs", checked: false },
  { id: "c3", name: "memes", label: "Memes", checked: false },
  { id: "c4", name: "brooklyn", label: "Brooklyn", checked: false }
];
const radioData = [
  { id: "p1", name: "pet", label: "Dogs", checked: false },
  { id: "p2", name: "pet", label: "Cats", checked: false }
];
class LoginComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      checkboxData: checkboxData,
      radioData: radioData
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleCheckbox = this.handleCheckbox.bind(this);
    this.handleRadio = this.handleRadio.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleCheckbox(e) {
    let newState;

    this.setState(prevState => {
      newState = prevState.checkboxData.map(val => {
        if (val.id === e) {
          console.log(val.id);
          let copy = { ...val };
          copy.checked = !copy.checked;
          return copy;
        }
        console.log(val.id);
        return val;
      });
      console.log(newState);
      return { checkboxData: newState };
    });
    console.log(e);
    console.log(this.state.checkboxData);
  }
  handleRadio(e) {
    console.log(e);
    let copyData;
    this.setState(prevState => {
      copyData = prevState.radioData.map(val => {
        let copy = { ...val };
        copy.checked = false;
        if (val.id === e) {
          copy.checked = true;
          return copy;
        }
        return copy;
      });
      return { radioData: copyData };
    });
  }
  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
    console.log(this.state);
  }
  handleSubmit() {
    this.setState(prevState => {
      console.log(prevState);
      return {
        firstName: "",
        lastName: "",
        checkboxData: checkboxData,
        radioData: radioData
      };
    });
  }
  render() {
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: "var(--background-main)" }}
      >
        <div className="text-section">
          <h1 className="title">Login</h1>
          {/* <h4 className="subtitle">Login</h4> */}
        </div>
        <form onSubmit={() => this.handleSubmit()}>
          <div className="form-row">
            <div className="input-group">
              <div className="label-style">Name</div>
              <div className="flex-row">
                <div className="col-grow">
                  <input
                    value={this.state.firstName}
                    name="firstName"
                    placeholder="first"
                    className="input-style"
                    onChange={e => this.handleChange(e)}
                  />
                </div>
                <div className="col-grow">
                  <input
                    value={this.state.lastName}
                    name="lastName"
                    placeholder="last"
                    className="input-style ml-2"
                    onChange={e => this.handleChange(e)}
                  />
                </div>
              </div>
            </div>
          </div>
          <div className="form-row">
            <div className="input-group radio-checkbox-input">
              <div className="label-style">Interests</div>
              <div className="checkbox-group-inline">
                {this.state.checkboxData.map(val => {
                  return (
                    <div className="custom-checkbox" key={val.id}>
                      <label htmlFor={val.name}>
                        <input
                          type="checkbox"
                          checked={val.checked}
                          id={val.name}
                          name={val.name}
                          onChange={e => {
                            this.handleCheckbox(val.id);
                          }}
                        />
                        <div className="checkbox">
                          <span className="inner-check"></span>
                        </div>
                        <div className="inner-label-text">{val.label}</div>
                      </label>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
          <div className="form-row">
            <div className="input-group radio-checkbox-input">
              <div className="label-style">Pets</div>
              <div className="radio-group">
                {this.state.radioData.map(val => {
                  return (
                    <div className="custom-radio" key={val.id}>
                      <input
                        type="radio"
                        checked={val.checked}
                        id={val.id}
                        name={val.name}
                        onChange={e => this.handleRadio(val.id)}
                        className="custom-control-input"
                      />
                      <label className="custom-control-label" htmlFor={val.id}>
                        <span className="inner-check"></span>
                        <span className="inner-label-text">{val.label}</span>
                      </label>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
          <button
            className="outline-btn accent"
            onClick={e => {
              console.log(this.state);
            }}
          >
            submit
          </button>
        </form>
      </article>
    );
  }
}

export default LoginComponent;
