import React from "react";

// import AlertingDivs from "./AlertingDivs";
import Tile from "./Tile";
import alertingData from "../../data/alertingData";
import alertArray from "../../data/alertingData";

const myData = [
  { title: "RRTWQ", number: 15, bkgd: "#fd5c5c" },
  { title: "AAQPR", number: 55, bkgd: "#fd5c5c" },
  { title: "NNNTRP", number: 32, bkgd: "#d48117" }
];
class AlertingCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      generalData: alertingData,
      tileData: myData,
      data: alertArray,
      unreadMessages: ["a", "b"]
    };
    // console.log("aalerting props", props);
  }
  render() {
    // console.log("state of alerting", this.props);
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: this.state.generalData.bkgd }}
      >
        <div className="text-section">
          <h1 className="title">{this.state.generalData.title}</h1>
          <h4 className="subtitle">{this.state.generalData.subtitle}</h4>
        </div>
        <div className="css-grid-tile-wrapper">
          {this.state.tileData.map((alert, i) => {
            return <Tile key={i} data={alert} active={this.props.active} />;
          })}
        </div>
        {this.state.unreadMessages.length > 0 && (
          <h2>You have {this.state.unreadMessages.length} unread messages!</h2>
        )}
        <div className="alertRowNew">
          <div class="row" data={this.state.data[0].urgent}>
            <div class="col alertTimeCol">
              <span class="timeOfAlert">{this.state.data[0].time}</span>
            </div>
            <div class="col messageCol">
              <span class="title">{this.state.data[0].title}</span>
              <span class="clientTicker">
                {this.state.data[0].client} - {this.state.data[0].ticker}{" "}
              </span>
              {this.state.data[0].message}
            </div>
            {/* <div class="col actionsCol">
                <div class="gip">GIP</div>
                <i id= "' + myIDalert + '" class="mdi mdi-check "></i>
            </div> */}
          </div>
        </div>

        {/* {props.data.divs} */}
        {/* <div
          style={{ backgroundColor: "yellow", width: "50%", height: "15px" }}
        ></div> */}
        {/* <div class="blurb-thumb">
          <p className="blurb">{props.data.blurb}</p>
          <div className="thumb">
            <img src={props.data.img} alt={props.data.subtitle} />
          </div>
        </div> */}
      </article>
    );
  }
}

export default AlertingCard;
