import React from "react";
const checkboxData = [
  { id: 1, label: "tickers", name: "Tickers", checked: false },
  { id: 2, label: "dogs", name: "Dogs", checked: false },
  { id: 3, label: "memes", name: "Memes", checked: false },
  { id: 4, label: "brooklyn", name: "Brooklyn", checked: false }
];
class LoginComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      checkboxData: checkboxData
    };

    this.handleChange = this.handleChange.bind(this);
  }
  handleCheckbox(e) {
    console.log(this.state.checkboxData);
  }
  handleChange(e) {
    const { name, value, type, checked } = e.target;
    // type === 'checkbox' ?
    // this.setState(prevState => {
    //   {
    //     checkboxData:
    //   }
    // }
    //  )    :
    this.setState({
      [name]: value
    });
    console.log(this.state);
  }
  render() {
    const myCheckboxData = this.state.checkboxData;
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: "var(--background-main)" }}
      >
        <div className="text-section">
          <h1 className="title">Login</h1>
          {/* <h4 className="subtitle">Login</h4> */}
        </div>
        <form>
          <input
            value={this.state.firstName}
            name="firstName"
            placeholder="first"
            className="input-style"
            onChange={e => this.handleChange(e)}
          />
          <input
            value={this.state.lastName}
            name="lastName"
            placeholder="last"
            className="input-style"
            onChange={e => this.handleChange(e)}
          />
          <div class="form-row">
            <div class="input-group radio-checkbox-input">
              <div class="label-style">Fruits</div>
              <div class="checkbox-group-inline">
                {myCheckboxData.map(val => {
                  return (
                    <div class="custom-checkbox" key={val.id}>
                      <label for={val.label}>
                        <input
                          type="checkbox"
                          checked={val.checked}
                          id={val.label}
                          name="fruits"
                          onChange={e => {
                            this.handleCheckbox(e);
                          }}
                        />
                        <div class="checkbox">
                          <span class="inner-check"></span>
                        </div>
                        <div class="inner-label-text">{val.name}</div>
                      </label>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
          <button
            className="outline-btn accent"
            onClick={e => {
              console.log(this.state);
            }}
          >
            submit
          </button>
        </form>
      </article>
    );
  }
}

export default LoginComponent;
