    let myVal = Object.entries(
      backupPlan[0]["Rank A: Real-Time Performance"]
    ).map(res => {
      return res[1];
    });
    let yellow = Common.mergeSort(myVal);
    console.log(yellow);
  }
  mapObject(res) {
    let tempArr = [];
    Object.entries(res).map((res, i) => {
      let temp = res[1].replace(/\%/, "");
      temp = parseFloat(temp);
      let isNegative = temp < 0 ? true : false;
      tempArr.push({
        name: res[0],
        val: temp,
        isNegative: isNegative
      });
    });
    return tempArr;
  }
  addWidth() {
    let newObject = this.mapObject(
      backupPlan[0]["Rank A: Real-Time Performance"]
