import React from "react";
function Nav (props) {
  function toggleTheme(theme) {
    props.setTheme(theme === "light" ? "dark" : "light");
  }



    return (
      <div className="navbar">
        <div
          className="nav-link"
          onClick={() => {
            props.poMansRouting("home");
          }}
        >
          BananaPhone
        </div>
        <div
          className="nav-link"
          onClick={() => {
            props.poMansRouting("dataViz");
          }}
        >
          DataViz
        </div>
        <div
          className="nav-link"
          onClick={() => {
            props.poMansRouting("portfolio");
          }}
        >
          Portfolio
        </div>
        <button
          className="sunlight-nighttime ml-auto"
          onClick={() => toggleTheme(props.myTheme)}
        >
          <div className="sun"></div> <div className="night"></div>
          Theme
        </button>
      </div>
    );

}

export default Nav;
