import React from "react";
import IsLoading from "../reusable/IsLoading";
import * as Common from "../reusable/common";
import StockData from "../../data/stock_data";
const imgCRB = require("../../assets/images/crb-img.png");
const data = {
  id: 3,
  img: imgCRB,
  title: "CRB Pricer",
  subtitle: "Central Risk Book Pricing and Trade Execution",
  blurb: [""],
  bkgd: "var(--crb-bkgd)"
};

const allTickers = StockData.slice();
class CRBCard extends React.Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.tickerInputRef = React.createRef();
    this.state = {
