      // let myDate = new Date();
      // console.log(myDate);
      let myKeys;
      if (loadingBool) {
        if (res !== undefined && res["Time Series (5min)"] !== undefined) {
          myKeys = Object.keys(res["Time Series (5min)"]);
          this.setState({
            isLoading: false,
            ticker: ticker,
            price: res["Time Series (5min)"][myKeys[0]]["1. open"]
          });
        } else {
          this.setState({
            isLoading: false,
            ticker: "Not a Ticker",
            price: null
          });
        }
      }
    });
  }
  getInputVal(event) {
    let tickerDDList = allTickers;
    // if (event === '') {
    //   this.tickerSymbol = '';
    //   this.tickerTitle = '';
    // }

    console.log(event.target.value);

