import React from "react";
const checkboxData = [
  { id: "c1", name: "tickers", label: "Tickers", checked: false },
  { id: "c2", name: "dogs", label: "Dogs", checked: false },
  { id: "c3", name: "memes", label: "Memes", checked: false },
  { id: "c4", name: "brooklyn", label: "Brooklyn", checked: false }
];
const radioData = [
  { id: "p1", name: "pet", value: "dogs", label: "Dogs", checked: false },
  { id: "p2", name: "pet", value: "cats", label: "Cats", checked: false }
];
class LoginComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      checkboxData: checkboxData
    };

    this.handleChange = this.handleChange.bind(this);
  }
  handleCheckbox(e) {
    let newState;
    // this.setState(prevState => {
    //   let myNewItems = prevState.todos.map(val => {
    //     if (val.id === id) {
    //       console.log("before val", val);
    //       let imNotAlteringState = { ...val };
    //       imNotAlteringState.completed = !imNotAlteringState.completed;
    //       console.log("after imNotAlteringState val", imNotAlteringState);
    //       return imNotAlteringState;
    //     }
    //     console.log(val);
    //     // this returns all the other vals
    //     return val;
    //   });
    //   // this is just typical of the set state function
    //   return { todos: myNewItems };
    // });
    this.setState(prevState => {
      newState = prevState.checkboxData.map(val => {
        if (val.id === e) {
          console.log(val.id);
          let copy = { ...val };
          copy.checked = !copy.checked;
          return copy;
          // return {
          //   [val.checked]: !val.checked,
          //   [val.id]: val.id,
          //   [val.name]: val.name,
          //   [val.label]: val.label
          // };
        }
        console.log(val.id);
        return val;
      });
      console.log(newState);
      return { checkboxData: newState };
    });
    console.log(e);
    console.log(this.state.checkboxData);
  }
  handleChange(e) {
    const { name, value } = e.target;

    // type === 'checkbox' ?
    // this.setState(prevState => {
    //   {
    //     checkboxData:
    //   }
    // }
    //  )    :
    this.setState({
      [name]: value
    });
    console.log(this.state);
  }
  render() {
    const myCheckboxData = this.state.checkboxData;
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: "var(--background-main)" }}
      >
        <div className="text-section">
          <h1 className="title">Login</h1>
          {/* <h4 className="subtitle">Login</h4> */}
        </div>
        <form>
          <div className="form-row">
            <div className="input-group">
              <div className="label-style">Name</div>
              <div className="flex-row">
                <div className="col-grow">
                  <input
                    value={this.state.firstName}
                    name="firstName"
                    placeholder="first"
                    className="input-style"
                    onChange={e => this.handleChange(e)}
                  />
                </div>
                <div className="col-grow">
                  <input
                    value={this.state.lastName}
                    name="lastName"
                    placeholder="last"
                    className="input-style ml-2"
                    onChange={e => this.handleChange(e)}
                  />
                </div>
              </div>
            </div>
          </div>
          <div className="form-row">
            <div className="input-group radio-checkbox-input">
              <div className="label-style">Interests</div>
              <div className="checkbox-group-inline">
                {myCheckboxData.map(val => {
                  return (
                    <div className="custom-checkbox" key={val.id}>
                      <label htmlFor={val.name}>
                        <input
                          type="checkbox"
                          checked={val.checked}
                          id={val.name}
                          name={val.name}
                          onChange={e => {
                            this.handleCheckbox(val.id);
                          }}
                        />
                        <div className="checkbox">
                          <span className="inner-check"></span>
                        </div>
                        <div className="inner-label-text">{val.label}</div>
                      </label>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
          <div className="form-row">
            <div className="input-group radio-checkbox-input">
              <div className="label-style">Radio Label</div>
              {radioData.map(() => {
                return (
                  <div className="radio-group">
                    <div className="custom-radio">
                      <input
                        type="radio"
                        id="customRadioInline3"
                        name="customRadioInline3"
                        className="custom-control-input"
                      />
                      <label
                        className="custom-control-label"
                        htmlFor="customRadioInline3"
                      >
                        <span className="inner-check"></span>
                        <span className="inner-label-text">Label One</span>
                      </label>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
          <button
            className="outline-btn accent"
            onClick={e => {
              console.log(this.state);
            }}
          >
            submit
          </button>
        </form>
      </article>
    );
  }
}

export default LoginComponent;
