import React from "react";
const checkboxData = [
  { id: "c1", name: "tickers", label: "Tickers", checked: false },
  { id: "c2", name: "dogs", label: "Dogs", checked: false },
  { id: "c3", name: "memes", label: "Memes", checked: false },
  { id: "c4", name: "brooklyn", label: "Brooklyn", checked: false }
];
const radioData = [
  { id: "p1", name: "pet", label: "Dogs", checked: false },
  { id: "p2", name: "pet", label: "Cats", checked: false }
];
let formData;
class WhiteRose extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "Angela",
      lastName: "Moss",
      checkboxData: checkboxData,
      radioData: radioData,
      keyInRoom: null,
      crySex: null
    };

    this.handleTextInput = this.handleTextInput.bind(this);
    this.handleCheckbox = this.handleCheckbox.bind(this);
    this.handleRadio = this.handleRadio.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleBoolean = this.handleBoolean.bind(this);
  }
  handleCheckbox(e) {
    let newState;

    this.setState(prevState => {
      newState = prevState.checkboxData.map(val => {
        if (val.id === e) {
          console.log(val.id);
          let copy = { ...val };
          copy.checked = !copy.checked;
          return copy;
        }
        console.log(val.id);
        return val;
      });
      console.log(newState);
      return { checkboxData: newState };
    });
    console.log(e);
    console.log(this.state.checkboxData);
  }
  handleRadio(e) {
    console.log(e);
    let copyData;
    this.setState(prevState => {
      copyData = prevState.radioData.map(val => {
        let copy = { ...val };
        copy.checked = false;
        if (val.id === e) {
          copy.checked = true;
          return copy;
        }
        return copy;
      });
      return { radioData: copyData };
    });
  }
  handleAngela(e) {
    const name = e.target.name;
    name === "firstName" && this.setState({ firstName: "Angela" });
    name === "lastName" && this.setState({ lastName: "Moss" });
  }
  handleTextInput(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
    // console.log(this.state);
  }
  handleBoolean(e) {
    const { id, checked, type, name } = e.target;
    if (type === "radio") {
      this.setState({
        [name]: id
      });
    }
    // console.log(id, checked, name, this.state.keyInRoom);
  }
  handleSubmit() {
    this.setState(prevState => {
      formData = prevState.firstName;
      return {
        firstName: "",
        lastName: "",
        checkboxData: checkboxData,
        radioData: radioData,
        keyInRoom: null
      };
    });
    return formData;
  }
  render() {
    return (
      <article
        className="portfolio-card"
        style={{
          backgroundPosition: "center",
          backgroundBlendMode: "hue",
          backgroundColor: "var(--background-main)",
          backgroundImage:
            "url('https://i.guim.co.uk/img/media/ccaebf266372711568c121dc12c12643e4130829/9_0_1193_716/master/1193.jpg?width=620&quality=45&auto=format&fit=max&dpr=2&s=a09ec5369cd929cb5dc3488903f8edec')"
        }}
      >
        <div className="text-section">
          <h1 className="title">whiteRose4Angela</h1>
          {/* <h4 className="subtitle">Login</h4> */}
        </div>
        {/* <form onSubmit={() => this.handleSubmit()}> */}
        <div className="form-row">
          <div className="input-group">
            <div className="label-style">Name</div>
            <div className="flex-row">
              <div className="col-grow">
                <input
                  value={this.state.firstName}
                  name="firstName"
                  placeholder="first"
                  className="input-style"
                  onChange={e => this.handleAngela(e)}
                />
              </div>
              <div className="col-grow">
                <input
                  value={this.state.lastName}
                  name="lastName"
                  placeholder="last"
                  className="input-style ml-2"
                  onChange={e => this.handleAngela(e)}
                />
              </div>
            </div>
          </div>
        </div>
        {/* <div className="form-row">
          <div className="input-group radio-checkbox-input">
            <div className="label-style">Interests</div>
            <div className="checkbox-group-inline">
              {this.state.checkboxData.map(val => {
                return (
                  <div className="custom-checkbox" key={val.id}>
                    <label htmlFor={val.name}>
                      <input
                        type="checkbox"
                        checked={val.checked}
                        id={val.name}
                        name={val.name}
                        onChange={e => {
                          this.handleCheckbox(val.id);
                        }}
                      />
                      <div className="checkbox">
                        <span className="inner-check"></span>
                      </div>
                      <div className="inner-label-text">{val.label}</div>
                    </label>
                  </div>
                );
              })}
            </div>
          </div>
        </div> */}
        <div className="form-row">
          <div className="input-group radio-checkbox-input">
            <div className="label-style">Do you cry during sex?</div>
            <div className="radio-group">
              <div className="custom-radio">
                <input
                  type="radio"
                  checked={this.state.crySex === "keyTrue"}
                  id="keyTrue"
                  name="crySex"
                  onChange={e => this.handleBoolean(e)}
                  className="custom-control-input"
                />
                <label className="custom-control-label" htmlFor="keyTrue">
                  <span className="inner-check"></span>
                  <span className="inner-label-text">Yes</span>
                </label>
              </div>
              <div className="custom-radio">
                <input
                  type="radio"
                  checked={this.state.crySex === "keyFalse"}
                  id="keyFalse"
                  name="crySex"
                  onChange={e => this.handleBoolean(e)}
                  className="custom-control-input"
                />
                <label className="custom-control-label" htmlFor="keyFalse">
                  <span className="inner-check"></span>
                  <span className="inner-label-text">No</span>
                </label>
              </div>
            </div>
          </div>
        </div>
        <div className="form-row">
          <div className="input-group radio-checkbox-input">
            <div className="label-style">Is the key in the room?</div>
            <div className="radio-group">
              <div className="custom-radio">
                <input
                  type="radio"
                  checked={this.state.keyInRoom === "keyTrue"}
                  id="keyTrue"
                  name="keyInRoom"
                  onChange={e => this.handleBoolean(e)}
                  className="custom-control-input"
                />
                <label className="custom-control-label" htmlFor="keyTrue">
                  <span className="inner-check"></span>
                  <span className="inner-label-text">Yes</span>
                </label>
              </div>
              <div className="custom-radio">
                <input
                  type="radio"
                  checked={this.state.keyInRoom === "keyFalse"}
                  id="keyFalse"
                  name="keyInRoom"
                  onChange={e => this.handleBoolean(e)}
                  className="custom-control-input"
                />
                <label className="custom-control-label" htmlFor="keyFalse">
                  <span className="inner-check"></span>
                  <span className="inner-label-text">No</span>
                </label>
              </div>
            </div>
          </div>
        </div>
        <div>
          <button
            className="outline-btn accent"
            onClick={() => {
              this.handleSubmit();
            }}
          >
            submit
          </button>
        </div>
        {/* </form> */}
        {formData}
      </article>
    );
  }
}

export default WhiteRose;
