import React from "react";

// import AlertingDivs from "./AlertingDivs";
import Tile from "./Tile";
const imgAlerting = require("../../images/alerting.png");
const data = {
  id: 4,
  img: imgAlerting,
  title: "Alerting",
  subtitle: "Client and Ticker Alerts for Traders",
  blurb: [""],
  bkgd: "var(--alerting-bkgd)"
};

const myData = [
  { title: "RRTWQ", number: 15, bkgd: "yellow" },
  { title: "AAQPR", number: 55, bkgd: "#fd5c5c" },
  { title: "NNNTRP", number: 32, bkgd: "teal" }
];
class AlertingCard extends React.Component {
  constructor() {
    super();
  }
  render() {
    return (
      <article
        className="portfolio-card"
        style={{ backgroundColor: data.bkgd }}
      >
        <div className="text-section">
          <h1 className="title">{data.title}</h1>
          <h4 className="subtitle">{data.subtitle}</h4>
        </div>
        <div className="css-grid-tile-wrapper">
          {myData.map((alert, i) => {
            return <Tile key={i} data={alert} />;
          })}
        </div>
        {/* {props.data.divs} */}
        {/* <div
          style={{ backgroundColor: "yellow", width: "50%", height: "15px" }}
        ></div> */}
        {/* <div class="blurb-thumb">
          <p className="blurb">{props.data.blurb}</p>
          <div className="thumb">
            <img src={props.data.img} alt={props.data.subtitle} />
          </div>
        </div> */}
      </article>
    );
  }
}

export default AlertingCard;
