      Materials: "1.30%",
      Industrials: "1.16%",
      "Information Technology": "1.06%",
      Financials: "0.77%",
      "Real Estate": "0.22%",
      "Health Care": "-0.33%",
      "Consumer Staples": "-0.48%",
      Utilities: "-0.88%"
    }
  }
];
class InsightAnalyticsCard extends React.Component {
  constructor(props) {
