export function debounce(func, wait, immediate) {
  var DEtimeout;
  return function() {
    var DEcontext = this,
      DEargs = arguments;
    var DElater = function() {
      DEtimeout = null;
      if (!immediate) {
        func.apply(DEcontext, DEargs);
      }
    };
    var DEcallNow = immediate && !DEtimeout;
    clearTimeout(DEtimeout);
    DEtimeout = setTimeout(DElater, wait);
    if (DEcallNow) {
      func.apply(DEcontext, DEargs);
    }
  };
}
