import React, { useState } from "react";

function MemeGenerator() {
  let memeDDConst;
  this.memeInputRef = React.createRef();
  this.ddRef = React.createRef();
  const memeApi = "https://api.imgflip.com/get_memes";

  const [allMemeImgs, setAllMemeImgs] = useState([]);
  const [memeDDFiltered, setMemeDDFiltered] = useState([]);
  const [memeImg, setMemeImg] = useState({});
  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");
  const [memeDDVisible, setMemeDDVisible] = useState(false);
  const [savedMemes, setSavedMemes] = useState([]);

  // function setAllMemeImgs(val) {
  //   allMemeImgs = val;
  // }
  // function setMemeDDFiltered(val) {
  //   memeDDFiltered = val;
  // }
  // function setMemeImg(val) {
  //   memeImg = val;
  // }
  // function setTopText(val) {
  //   topText = val;
  // }
  // function setBottomText(val) {
  //   bottomText = val;
  // }
  // function setMemeDDVisible(val) {
  //   memeDDVisible = val;
  // }
  // function setSavedMemes(val) {
  //   savedMemes = val;
  // }

  function handleChange() {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
    // console.log(this.state);
  }

  function handleMemeInput(e) {
    let myResults;
    // this.setState({
    //   memeInput: e.target.value
    // });
    console.log(memeDDConst);
    if (e !== null || e !== "") {
      myResults = memeDDConst.filter(function(myKey) {
        return myKey["name"]
          .toLowerCase()
          .startsWith(e.target.value.toLowerCase());
      });
      console.log(myResults);
      setMemeDDFiltered(myResults);
      setMemeInput(e.target.value);

      // this.ddRef.current.childNodes[0].focus();
      // console.log(this.ddRef.current);
    }
  }
  function eraseInput() {
    console.log(this.memeInputRef);
    this.memeInputRef.current.focus();
    setMemeDDFiltered(memeDDConst);
    setMemeInput("");
    setMemeDDVisible(true);
  }
  //   handleKeyUp(e) {
  //     console.log(e.which);
  //     this.setState({
  //       memeDDVisible: true
  //     });
  //     // if (e.which === 40) {
  //     //   this.ddRef.current.children[ddListIndex].focus();
  //     // }
  //   }
  function handleInputClick() {
    setMemeDDFiltered(memeDDConst);
    setMemeDDVisible(true);
  }
  function handleDDClick(val) {
    console.log(val);
    setMemeDDVisible(false);
    setMemeInput(val.name);
    setMemeImg(val);
  }
  function getAllMemes(memes) {
    setAllMemeImgs(memes);
    setMemeImg(memes[0]);

    memeDDConst = memes;
    // console.log(this.state);
    // console.log(allMemeImgs[0]);
  }
  function handleSubmit(e) {
    e.preventDefault();
    // this.setState(prevState => {
    //   formData = prevState.firstName;
    //   return {
    //     firstName: "",
    //     lastName: "",
    //     checkboxData: checkboxData,
    //     radioData: radioData
    //   };
    // });
    // return formData;
  }
  function saveMemes(e) {
    e.preventDefault();
    this.setState(prevState => {
      console.log(prevState);
      let newList = { ...prevState.savedMemes };
      console.log(newList);
      //   newList.push({
      //     topText: topText,
      //     bottomText: bottomText,
      //     memeImg: memeImg["url"],
      //     name: memeImg["name"]
      //   });
      return {
        savedMemes: newList
      };
    });
    // console.log(this.state);
  }
  function componentDidMount() {
    fetch(memeApi)
      .then(res => res.json())
      .then(res => {
        console.log(res.data.memes);

        getAllMemes(res.data.memes);
      });
  }
  componentDidMount();
  return (
    <article
      className="portfolio-card"
      style={{ backgroundColor: "var(--background-main)" }}
    >
      <div className="text-section">
        <h1 className="title">MemeGenerator</h1>
        {/* <h4 className="subtitle">Login</h4> */}
      </div>
      <form onSubmit={e => handleSubmit(e)}>
        <div className="input-group">
          <div className="relative">
            <div className="clearable">
              <input
                ref={memeInputRef}
                className="input-style"
                type="text"
                placeholder="search meme"
                name="memeInput"
                autoComplete="off"
                value={state.memeInput}
                onChange={e => {
                  handleMemeInput(e);
                }}
                onClick={() => {
                  handleInputClick();
                }}
              />

              {memeInput !== "" ? (
                <span
                  className="clearable-clear mdi mdi-close"
                  onClick={() => {
                    eraseInput();
                  }}
                ></span>
              ) : null}
              {memeDDVisible && (
                <div className="dropdown-container" ref={ddRef}>
                  {memeDDFiltered !== "" &&
                    memeDDFiltered.map((val, i) => {
                      return (
                        <button
                          key={i}
                          onClick={e => {
                            handleDDClick(val);
                          }}
                          // value={this.selectedTicker}
                        >
                          <div
                            className="meme-dd-div"
                            style={{
                              backgroundImage: `url(${val["url"]})`
                            }}
                          ></div>
                          - {val.name}
                        </button>
                      );
                    })}
                </div>
              )}
            </div>
          </div>
        </div>
        <div className="mt-2">
          <div className="input-group mt-2">
            <div className="label-style">Top Text</div>
            <input
              className="input-style"
              name="topText"
              value={topText}
              type="text"
              autoComplete="off"
              onChange={e => handleChange(e)}
            />
          </div>
          <div className="input-group mt-2">
            <div className="label-style">Bottom Text</div>
            <input
              className="input-style"
              name="bottomText"
              value={bottomText}
              type="text"
              autoComplete="off"
              onChange={e => handleChange(e)}
            />
          </div>
          <div
            className="meme-div mt-2"
            style={{ backgroundImage: `url(${memeImg["url"]})` }}
          >
            <div className="meme-text">{topText}</div>
            <div className="meme-text">{bottomText}</div>
          </div>
          <small className="mt-1"> {memeImg["name"]}</small>
        </div>
        <div className="mt-2">
          <button className="outline-btn accent">save</button>
        </div>
      </form>
    </article>
  );
}

export default MemeGenerator;
